data = {
  const co2data = await FileAttachment('https://raw.githubusercontent.com/wf-id/rsv-tracker/main/output/rsv_all_combined_latest.csv')
    .csv({ typed: true });
  return co2data.map(d => { 
    d["Label"] = d["state"] + ' ' + d["percent_positive"];
    return d; 
  });
}






